    private void convert(string filePath, string outputFilePath)
    {
        try
        {
            byte[] byteArray = File.ReadAllBytes(filePath);
            string[] hexArray = Array.ConvertAll(byteArray, b => "0x" + b.ToString("X2"));
            string hexCodes = string.Join(", ", hexArray);
            File.WriteAllText(
                outputFilePath,
                "static const unsigned char rawdata[] = {" + hexCodes + "};"
            );
            MessageBox.Show(
                "File converted successfully!" + "\nOutput file: " + outputFilePath,
                "Bin2C",
                MessageBoxButtons.OK,
                MessageBoxIcon.Information
            );
        }
        catch (Exception ex)
        {
            MessageBox.Show(
                "Error converting file: " + ex.Message + "\nTry running as administrator.",
                "Bin2C",
                MessageBoxButtons.OK,
                MessageBoxIcon.Error
            );
        }
    }

    private void selectButton_Click(object sender, EventArgs e)
    {
        OpenFileDialog openFileDialog = new OpenFileDialog();
        openFileDialog.Filter = "All Files (*.*)|*.*";
        openFileDialog.InitialDirectory = Environment.GetFolderPath(
            Environment.SpecialFolder.MyComputer
        );

        if (openFileDialog.ShowDialog() == DialogResult.OK)
        {
            string filePath = openFileDialog.FileName;
            textBox.Text = filePath;
            convertButton.Enabled = true;
        }
    }

    private void convertButton_Click(object sender, EventArgs e)
    {
        convert(textBox.Text, textBox.Text + ".h");
    }

    private void aboutButton_Click(object sender, EventArgs e)
    {
        MessageBox.Show(
            "Bin2C is a simple tool that converts binary files to C arrays.\n\n"
                + "Author: @valium007\n",
            "About Bin2C",
            MessageBoxButtons.OK,
            MessageBoxIcon.Information
        );
    }
